function evaluateProperty({    api,    state,    helpers}) {
    console.log("EVALUATE PROPERTY for Version 1");
    const selectedVersion2 = api.state.version_2[0];
    console.log("selectedVersion2 = ", selectedVersion2);
    const filteredList = api.data.getkbversions.output.filter(version => version.id !== selectedVersion2);
