sys_ux_data_broker_transform.description:
Returns all the versions of a Knowledge Base (KB) article.
Usage: 
var input = {    KB_Number: "KB10000000"};
var versions = transform(input);

gs.info("@UIB: getKbVersions \n" +
        "Input: " + JSON.stringify(input, null, 4) + "\n" +
        "Results: " + JSON.stringify(versions, null, 4));

sys_ux_data_broker_transform.required_translations:
[ {
  "message" : "KB Number",
  "comment" : ""
}, {
  "message" : "Number of the Knowledge Base (KB) article",
  "comment" : ""
} ]

sys_ux_data_broker_transform.script:
function transform(input) {

    var kb_number = input.KB_Number;

    var kb_record = new GlideRecord('kb_knowledge');
    kb_record.addQuery('number', kb_number);
    kb_record.orderBy('sys_created_on');
    kb_record.query();

    var versions = [];

    while (kb_record.next()) {
        var version = kb_record.version.version + "";
        versions.push({
            id: version,
            label: "Version " + version,
            sys_id: kb_record.getValue("sys_id")
        });
    }
    return versions;
}
